useEffect(() => {
   const fetchData = async () => {
     setIsLoading(true);
     try {
       const data = await getPostById(postId);
       setPost(data);
       setError(null);
     } catch (error) {
       setError(error);
       setPost(null);
     }
     setIsLoading(false);
   };
   fetchData();
 }, [postId]);
